import React, { Component } from 'react';
import CustomButton from './CustomButton';

class Counter extends Component{
        state = {
            count: 0,
            disable
        };

        handleIncrement = () => {
            this.setState({count: this.state.count + 1 })
        };

        handleDecrement = () => {
            this.setState( { count: this.state.count - 1 } )
            this.setState({disable : "disable"});
        };

    render(){
        return(


            <div>

        
            
            <CustomButton  type="button" onClick={this.handleIncrement} className="btn btn-secondary btn-sm m-4 ">Increment</CustomButton>

            <span className = "count-num badge bg-primary m-5">{this.state.count}</span>

            <CustomButton disable type="button" onClick={this.handleDecrement} className="btn btn-secondary btn-sm ">Decrement</CustomButton>



            </div>

        )
    }
}
export default Counter;
