import React, { Component } from 'react';
import CustomButton from './CustomButton';
import PropTypes from "prop-types";
class Counter extends Component{
        
        static propTypes = {
            count: PropTypes.number,
            disabled:  PropTypes.string
          };

        handleIncrement = () => {
            this.setState({count: this.props.count + 1 });
            this.setState({disable: ""});
        };

        handleDecrement = () => {
            if(this.props.count > 0)
            {
                this.setState( { count: this.props.count - 1 } );
            }
            if(this.props.count <= 1)
            {
                     this.setState({disable : "disabled"});
            }
        };

    render(){
        return(


            <div>
    
                <CustomButton   type="button" onClick={this.handleIncrement} className="btn btn-secondary btn-sm m-4 ">Increment</CustomButton>

                <span className = "count-num badge bg-primary m-5">{this.props.count}</span>

                <CustomButton disabled={this.props.disable} type="button" onClick={this.handleDecrement} className="btn btn-secondary btn-sm ">Decrement</CustomButton>



            </div>

        )
    }
}
export default Counter;
